"use client";

import React, { useState, useEffect } from "react";
import Select from "react-select";
import "../styles/AutoGameRoomMatching.css";
import axios from "axios";

// Ïã§Ï†ú ÏÑúÎπÑÏä§ DBÏóê Îì±Î°ùÎêú Ïú†Ï†ÄÏùò idÎ°ú ÍµêÏ≤¥!
const userId = 1; // ÏòàÏãú, Ï∂îÌõÑ Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥ÏôÄ Ïó∞Îèô Í∞ÄÎä•

const sampleVenues = [
  {
    courtName: "downtown",
    courtAddress: "Í≤ΩÏÉÅÎ∂ÅÎèÑ Í≤ΩÏÇ∞Ïãú Ïò•ÏÇ∞Î°ú 120",
    latitude: 35.816742,
    longitude: 128.742983,
  },
  {
    courtName: "ÏÇºÏÑ±ÌòÑÍ≥µÏõê",
    courtAddress: "Í≤ΩÏÇ∞Ïãú ÏÇºÏÑ±ÌòÑÎ°ú 45",
    latitude: 35.831201,
    longitude: 128.73012,
  },
  {
    courtName: "ÏòÅÎÇ®ÎåÄ Ïã§ÎÇ¥Ï≤¥Ïú°Í¥Ä",
    courtAddress: "Í≤ΩÎ∂Å Í≤ΩÏÇ∞Ïãú ÎåÄÌïôÎ°ú 280",
    latitude: 35.883733,
    longitude: 128.813515,
  },
];

const AutoGameRoomMatching = () => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [matchType, setMatchType] = useState("Neighborhood");
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split("T")[0]);
  const [selectedTime, setSelectedTime] = useState("18:00");
  const [selectedVenue, setSelectedVenue] = useState(sampleVenues[0].courtName);
  const [maxDistance] = useState(300);
  const [selectedGameType, setSelectedGameType] = useState("Doubles");
  const [autoGenerationEnabled, setAutoGenerationEnabled] = useState(true);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [matchSuccess, setMatchSuccess] = useState(false);
  const [matchedOpponent, setMatchedOpponent] = useState(null);
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);

  const selectedVenueObj = sampleVenues.find((v) => v.courtName === selectedVenue);

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  // 30Î∂Ñ Îã®ÏúÑ ÏòµÏÖò
  const timeOptions = [];
  for (let hour = 0; hour < 24; hour++) {
    timeOptions.push(`${hour.toString().padStart(2, "0")}:00`);
    timeOptions.push(`${hour.toString().padStart(2, "0")}:30`);
  }
  const customTimeOptions = timeOptions.map((time) => ({ value: time, label: time }));

  // --------------- Î©îÏù∏ Î°úÏßÅ: ÌÅê Îì±Î°ù -> Îß§Ïπ≠ ÏöîÏ≤≠ ---------------
  const handleMatching = async () => {
    setIsModalOpen(true);
    setMatchSuccess(false);
    setMatchedOpponent(null);

    const requiredMatchCount = selectedGameType === "Singles" ? 2 : 4;

    const queuePayload = {
      date: selectedDate,
      time: selectedTime,
      location: {
        courtName: selectedVenueObj.courtName,
        courtAddress: selectedVenueObj.courtAddress,
        latitude: selectedVenueObj.latitude,
        longitude: selectedVenueObj.longitude,
      },
      requiredMatchCount,
    };

    try {
      // 1. ÌÅê Îì±Î°ù(ÏÇ¨Ï†Ñ)
      await axios.post(
        `/api/match/auto/queue/location-preGym?userId=${userId}`,
        queuePayload
      );

      // 2. Îß§Ïπ≠ ÏöîÏ≤≠
      let matchApiUrl = "";
      if (matchType === "Neighborhood") {
        matchApiUrl = `/api/match/auto/rooms/location?userId=${userId}`;
      } else {
        matchApiUrl = `/api/match/auto/rooms/gym?userId=${userId}`;
      }

      const res = await axios.post(matchApiUrl);

      setMatchedOpponent(res.data.userIds || []);
      setMatchSuccess(true);
      setShowSuccessPopup(true);
      setTimeout(() => setShowSuccessPopup(false), 2000);
    } catch (err) {
      setMatchSuccess(false);
      setMatchedOpponent(null);

      let errMsg = "ÌÅê Îì±Î°ù ÎòêÎäî Îß§Ïπ≠Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.";
      if (err.response && err.response.data) {
        if (typeof err.response.data === "string") {
          errMsg = err.response.data;
        } else if (err.response.data.message) {
          errMsg = err.response.data.message;
        }
      }
      alert(errMsg);
    }
  };

  // ÌÅê Îì±Î°ù Ï∑®ÏÜå (API Ìò∏Ï∂ú)
  const handleQueueCancel = async () => {
    try {
      await axios.delete(`/api/match/auto/queue?userId=${userId}`);
      alert("ÌÅê Îì±Î°ùÏù¥ Ï∑®ÏÜåÎêòÏóàÏäµÎãàÎã§.");
      window.location.href = "/pre-matching";
    } catch (err) {
      let errMsg = "ÌÅê Îì±Î°ù Ï∑®ÏÜåÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.";
      if (err.response && err.response.data) {
        if (typeof err.response.data === "string") {
          errMsg = err.response.data;
        } else if (err.response.data.message) {
          errMsg = err.response.data.message;
        }
      }
      alert(errMsg);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    window.location.href = "/pre-matching";
  };

  const handleLeave = () => {
    window.location.href = "/pre-matching";
  };

  return (
    <div className="agr-auto-matching-container">
      <div className="agr-auto-matching-header">
        <h2>ÏûêÎèô GameRoom Îß§Ïπ≠</h2>
        <div className="agr-auto-matching-time">üïê {currentTime.toLocaleTimeString()}</div>
      </div>
      <label className="agr-auto-matching-checkbox">
        <input
          type="checkbox"
          checked={autoGenerationEnabled}
          onChange={(e) => setAutoGenerationEnabled(e.target.checked)}
        />
        ÏûêÎèô Í≤åÏûÑÎ∞© ÏÉùÏÑ± ÌóàÏö©
      </label>
      <div className="agr-match-type-buttons">
        <button
          className={`agr-match-type-button ${matchType === "Neighborhood" ? "active" : ""}`}
          onClick={() => setMatchType("Neighborhood")}
        >
          ÎèôÎÑ§Îß§Ïπ≠
        </button>
        <button
          className={`agr-match-type-button ${matchType === "Court" ? "active" : ""}`}
          onClick={() => setMatchType("Court")}
        >
          Íµ¨Ïû•Îß§Ïπ≠
        </button>
      </div>

      <div className="agr-auto-matching-grid">
        <div>
          <label className="agr-auto-matching-label">ÎÇ†Ïßú</label>
          <input
            type="date"
            className="agr-auto-matching-input"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
          />
        </div>
        <div>
          <label className="agr-auto-matching-label">ÏãúÍ∞Ñ</label>
          <Select
            options={customTimeOptions}
            value={customTimeOptions.find((opt) => opt.value === selectedTime)}
            onChange={(option) => setSelectedTime(option.value)}
            menuPlacement="bottom"
            styles={{
              container: (provided) => ({ ...provided, width: "180px" }),
              menu: (provided) => ({ ...provided, zIndex: 9999 }),
              control: (provided) => ({ ...provided, minHeight: "38px" }),
            }}
            placeholder="ÏãúÍ∞Ñ ÏÑ†ÌÉù"
            isSearchable={false}
          />
        </div>
        <div>
          <label className="agr-auto-matching-label">Ïû•ÏÜå</label>
          <select
            className="agr-auto-matching-select"
            value={selectedVenue}
            onChange={(e) => setSelectedVenue(e.target.value)}
          >
            {sampleVenues.map((venue, idx) => (
              <option key={idx} value={venue.courtName}>
                {venue.courtName}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label className="agr-auto-matching-label">ÏµúÎåÄ Í±∞Î¶¨: {maxDistance}m</label>
          <input
            type="range"
            min="50"
            max="300"
            step="50"
            value={maxDistance}
            className="agr-auto-matching-input"
            disabled
          />
        </div>
        <div>
          <label className="agr-auto-matching-label">Í≤åÏûÑ Ïú†Ìòï</label>
          <select
            className="agr-auto-matching-select"
            value={selectedGameType}
            onChange={(e) => setSelectedGameType(e.target.value)}
          >
            <option value="Singles">Îã®Ïãù (1v1)</option>
            <option value="Doubles">Î≥µÏãù (2v2)</option>
          </select>
        </div>
      </div>

      {/* Î≤ÑÌäº */}
      <div style={{ display: "flex", justifyContent: "center", gap: "16px", marginTop: "20px" }}>
        <button className="agr-create-button" onClick={handleMatching}>
          GameRoom ÏÉùÏÑ±
        </button>
        <button className="agr-leave-button" onClick={handleLeave}>
          ÎÇòÍ∞ÄÍ∏∞
        </button>
      </div>

      <div className="agr-auto-matching-info">
        <p>üí° Ï°∞Í±¥Ïóê ÎßûÎäî Í≤åÏûÑÎ∞©Ïù¥ ÏûêÎèôÏúºÎ°ú ÏÉùÏÑ±Îê©ÎãàÎã§.</p>
        <ul>
          <li>ÏÑ†ÌÉùÌïú ÎÇ†ÏßúÏôÄ ÏãúÍ∞ÑÎåÄ ÎèôÏùºÌïú ÏÇ¨Ïö©ÏûêÏôÄ Îß§Ïπ≠</li>
          <li>300m Ïù¥ÎÇ¥Ïùò ÏúÑÏπòÏóêÏÑú Îß§Ïπ≠</li>
          <li>ÏÑ†ÌÉùÌïú Í≤åÏûÑ Ïú†ÌòïÏóê Îî∞Îùº Ïù∏ÏõêÏù¥ Ï∂©Ï°±ÎêòÎ©¥ ÏûêÎèô ÏÉùÏÑ±</li>
        </ul>
      </div>

      {/* Î™®Îã¨ */}
      {isModalOpen && (
        <div className="agr-modal-overlay">
          <div className="agr-modal-content">
            <h3>üìã ÏÑ†ÌÉùÌïú Ï†ïÎ≥¥</h3>
            <p>ÎÇ†Ïßú: {selectedDate}</p>
            <p>ÏãúÍ∞Ñ: {selectedTime}</p>
            <p>Ïû•ÏÜå: {selectedVenueObj.courtName}</p>
            <p>Ï£ºÏÜå: {selectedVenueObj.courtAddress}</p>
            <p>Í≤åÏûÑ Ïú†Ìòï: {selectedGameType === "Singles" ? "Îã®Ïãù" : "Î≥µÏãù"}</p>
            <hr style={{ margin: "16px 0" }} />
            {matchSuccess && matchedOpponent ? (
              <>
                <h4>üßë‚Äçü§ù‚Äçüßë Îß§Ïπ≠ ÏÉÅÎåÄ</h4>
                <p>{JSON.stringify(matchedOpponent)}</p>
              </>
            ) : (
              <p>‚ú® Îß§Ïπ≠ ÏÉÅÎåÄ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
            )}
            <div style={{ display: "flex", justifyContent: "center", gap: "14px", marginTop: "24px" }}>
              <button className="agr-close-button" onClick={closeModal}>
                Îã´Í∏∞
              </button>
              <button className="agr-close-button" onClick={handleQueueCancel}>
                ÌÅê Îì±Î°ù Ï∑®ÏÜå
              </button>
            </div>
          </div>
        </div>
      )}

      {showSuccessPopup && (
        <div className="agr-success-popup">üéâ Îß§Ïπ≠Ïù¥ ÏÑ±Í≥µÎêòÏóàÏäµÎãàÎã§!</div>
      )}
    </div>
  );
};

export default AutoGameRoomMatching;
