import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/PreMatching.css';
import logo from '../assets/shuttleplay_main_logo.png';
import AutoGameRoomMatching from './AutoGameRoomMatching';
import { useSelector } from 'react-redux';
import axios from 'axios';

// Î∞© ÏÉùÏÑ± Î™®Îã¨
function PreMatchingCreateRoomModal({ open, onClose, onCreate }) {
  const [mode, setMode] = useState('Íµ¨Ïû•');

  const [formGym, setFormGym] = useState({
    venue: '', address: '', latitude: '', longitude: '',
    courtName: '', courtAddress: '', date: '', time: '',
  });

  const [formLocal, setFormLocal] = useState({
    location: '', latitude: '', longitude: '', date: '', time: '',
  });

  useEffect(() => {
    if (open) {
      setMode('Íµ¨Ïû•');
      setFormGym ({ venue:'', address:'', latitude:'', longitude:'', courtName:'', courtAddress:'', date:'', time:'' });
      setFormLocal({ location:'', latitude:'', longitude:'', date:'', time:'' });
    }
  }, [open]);

  const handleChangeGym = e => setFormGym (p=>({...p,[e.target.name]:e.target.value}));
  const handleChangeLocal = e => setFormLocal(p=>({...p,[e.target.name]:e.target.value}));

  const handleSubmit = e => {
    e.preventDefault();
    mode === 'Íµ¨Ïû•'
      ? onCreate({ ...formGym,  mode:'Íµ¨Ïû•Îß§Ïπ≠' })
      : onCreate({ ...formLocal,mode:'ÎèôÎÑ§Îß§Ïπ≠' });
    onClose();
  };

  if (!open) return null;

  return (
    <div className="pre-modal-backdrop">
      <div className="pre-modal">
        <div style={{display:'flex',gap:12,marginBottom:20,justifyContent:'center'}}>
          <button className={`pre-modal-mode-btn${mode==='Íµ¨Ïû•'?' selected':''}`} onClick={()=>setMode('Íµ¨Ïû•')}>
            Íµ¨Ïû• Í≤åÏûÑÎ∞© ÏÉùÏÑ±
          </button>
          <button className={`pre-modal-mode-btn${mode==='ÎèôÎÑ§'?' selected':''}`} onClick={()=>setMode('ÎèôÎÑ§')}>
            ÎèôÎÑ§ Í≤åÏûÑÎ∞© ÏÉùÏÑ±
          </button>
        </div>

        <form className="pre-modal-form" onSubmit={handleSubmit}>
          {mode==='Íµ¨Ïû•' ? (
            <>
              <input name="venue" placeholder="Ïû•ÏÜåÎ™Ö" value={formGym.venue} onChange={handleChangeGym} required/>
              <input name="address" placeholder="ÏßÄÏó≠Î™Ö" value={formGym.address} onChange={handleChangeGym} required/>
              <input name="latitude" placeholder="ÏúÑÎèÑ" value={formGym.latitude} onChange={handleChangeGym} required/>
              <input name="longitude" placeholder="Í≤ΩÎèÑ" value={formGym.longitude} onChange={handleChangeGym} required/>
              <input name="courtName" placeholder="Ï≤¥Ïú°Í¥Ä Ïù¥Î¶Ñ" value={formGym.courtName} onChange={handleChangeGym} required/>
              <input name="courtAddress" placeholder="Ï≤¥Ïú°Í¥Ä Ï£ºÏÜå" value={formGym.courtAddress} onChange={handleChangeGym} required/>
              <input name="date" type="date" placeholder="ÎÇ†Ïßú" value={formGym.date} onChange={handleChangeGym} required/>
              <input name="time" type="time" placeholder="ÏãúÍ∞Ñ" value={formGym.time} onChange={handleChangeGym} required/>
            </>
          ) : (
            <>
              <input name="location" placeholder="ÎèôÎÑ§(ÏßÄÏó≠Î™Ö)" value={formLocal.location} onChange={handleChangeLocal} required/>
              <input name="latitude" placeholder="ÏúÑÎèÑ" value={formLocal.latitude} onChange={handleChangeLocal} required/>
              <input name="longitude" placeholder="Í≤ΩÎèÑ" value={formLocal.longitude} onChange={handleChangeLocal} required/>
              <input name="date" type="date" placeholder="ÎÇ†Ïßú" value={formLocal.date} onChange={handleChangeLocal} required/>
              <input name="time" type="time" placeholder="ÏãúÍ∞Ñ" value={formLocal.time} onChange={handleChangeLocal} required/>
            </>
          )}
          <button className="pre-modal-create-btn" type="submit">Î∞© ÏÉùÏÑ±</button>
          <button className="pre-modal-cancel-btn" type="button" onClick={onClose}>Îã´Í∏∞</button>
        </form>
      </div>
    </div>
  );
}

// Î©îÏù∏ Ïª¥Ìè¨ÎÑåÌä∏
function PreMatching() {
  const navigate = useNavigate();
  const userId = useSelector(s=>s.auth.user?.userId);
  const [step, setStep] = useState('selectMode');
  const [selectedMode, setSelectedMode] = useState('Íµ¨Ïû•Îß§Ïπ≠');
  const [selectedVenue, setSelectedVenue] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTime, setSelectedTime] = useState('');
  const [createModalOpen, setCreateModalOpen] = useState(false);
  const [roomList, setRoomList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Íµ¨Ïû•/ÎèôÎÑ§ ÏòµÏÖò Î∞è location Í∞í ÌïòÎìúÏΩîÎî©
  const gymOptions = [
    {
      label: 'ÏòÅÎÇ®ÎåÄÌïôÍµê Ïù¥Ìù¨Í±¥ Í∏∞ÎÖêÍ¥Ä',
      value: 'ÏòÅÎÇ®ÎåÄÌïôÍµê Ïù¥Ìù¨Í±¥ Í∏∞ÎÖêÍ¥Ä',
      courtName: 'Ïù¥Ìù¨Í±¥ Í∏∞ÎÖêÍ¥Ä',
      courtAddress: 'Í≤ΩÎ∂Å Í≤ΩÏÇ∞Ïãú ÎåÄÌïôÎ°ú 280',
      latitude: '35.8835',
      longitude: '128.8172',
      address: 'Í≤ΩÎ∂Å Í≤ΩÏÇ∞Ïãú ÎåÄÌïôÎ°ú 280'
    },
    {
      label: 'Í≤ΩÏÇ∞ Ïã§ÎÇ¥ Ï≤¥Ïú°Í¥Ä',
      value: 'Í≤ΩÏÇ∞ Ïã§ÎÇ¥ Ï≤¥Ïú°Í¥Ä',
      courtName: 'Í≤ΩÏÇ∞ Ïã§ÎÇ¥ Ï≤¥Ïú°Í¥Ä',
      courtAddress: 'Í≤ΩÎ∂Å Í≤ΩÏÇ∞Ïãú Ï§ëÏïôÎ°ú 246',
      latitude: '35.8251',
      longitude: '128.7376',
      address: 'Í≤ΩÎ∂Å Í≤ΩÏÇ∞Ïãú Ï§ëÏïôÎ°ú 246'
    }
  ];
  const localOptions = [
    {
      label: 'Í≤ΩÏÇ∞Ïãú Ï°∞ÏòÅÎèô',
      value: 'Í≤ΩÏÇ∞Ïãú Ï°∞ÏòÅÎèô',
      location: 'Í≤ΩÏÇ∞Ïãú Ï°∞ÏòÅÎèô',
      courtName: 'Í≤ΩÏÇ∞Ïãú Ï°∞ÏòÅÎèô',
      courtAddress: 'Í≤ΩÏÇ∞Ïãú Ï°∞ÏòÅÎèô',
      latitude: '35.8381',
      longitude: '128.7552'
    },
    {
      label: 'Í≤ΩÏÇ∞Ïãú ÏïïÎüâÏùç',
      value: 'Í≤ΩÏÇ∞Ïãú ÏïïÎüâÏùç',
      location: 'Í≤ΩÏÇ∞Ïãú ÏïïÎüâÏùç',
      courtName: 'Í≤ΩÏÇ∞Ïãú ÏïïÎüâÏùç',
      courtAddress: 'Í≤ΩÏÇ∞Ïãú ÏïïÎüâÏùç',
      latitude: '35.8999',
      longitude: '128.7962'
    }
  ];

  // Ïû•ÏÜå ÏÑ†ÌÉù Ïãú location Í∞í ÏûêÎèô ÏÑ∏ÌåÖ
  const [selectedLocation, setSelectedLocation] = useState({});

  useEffect(() => {
    if (selectedMode === 'Íµ¨Ïû•Îß§Ïπ≠') {
      const found = gymOptions.find(opt => opt.value === selectedVenue);
      setSelectedLocation(found || {});
    } else {
      const found = localOptions.find(opt => opt.value === selectedVenue);
      setSelectedLocation(found || {});
    }
  }, [selectedVenue, selectedMode]);

  const handleFindRooms = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setRoomList([]);
    try {
      let url = '';
      let body = {};
      if (selectedMode === 'Íµ¨Ïû•Îß§Ïπ≠') {
        url = `/api/match/manual/rooms/gym?userId=${userId}`;
        body = {
          location: {
            courtName: selectedLocation.courtName || '',
            courtAddress: selectedLocation.courtAddress || '',
            latitude: selectedLocation.latitude || '',
            longitude: selectedLocation.longitude || '',
            address: selectedLocation.address || ''
          },
          date: selectedDate,
          time: selectedTime,
        };
      } else {
        url = `/api/match/manual/rooms/location?userId=${userId}`;
        body = {
          location: {
            courtName: selectedLocation.courtName || '',
            courtAddress: selectedLocation.courtAddress || '',
            latitude: selectedLocation.latitude || '',
            longitude: selectedLocation.longitude || '',
            // Î™ÖÏÑ∏ÏÉÅ location(Ïù¥Î¶Ñ)ÎèÑ Í∞ôÏù¥ Î≥¥ÎÉÑ
            location: selectedLocation.location || ''
          },
          date: selectedDate,
          time: selectedTime,
        };
      }
      const res = await axios.post(url, body, {
        headers: { userId },
        withCredentials: true
      });
      setRoomList(res.data.rooms || []);
    } catch (err) {
      setError('Í≤åÏûÑÎ∞© Ï°∞ÌöåÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
    setLoading(false);
  };

  // ÏãúÍ∞Ñ ÏòµÏÖò
  const timeOptions = [];
  for(let h=0;h<24;h++){
    timeOptions.push(`${String(h).padStart(2,'0')}:00`);
    timeOptions.push(`${String(h).padStart(2,'0')}:30`);
  }

  const getBtnClass = active => 'pre-select-mode-btn' + (active ? ' pre-select-active' : '');

  // Î†åÎçîÎßÅ
  return (
    <div className="pre-matching-wrapper">
      <Header/>

      <div className="pre-body-center">
        <PreMatchingCreateRoomModal
          open={createModalOpen}
          onClose={() => setCreateModalOpen(false)}
          onCreate={() => {}}
        />

        {/* Ï≤òÏùå ÎÇòÏò§Îäî ÏûêÎèô/ÏàòÎèô Îß§Ïπ≠ ÏÑ†ÌÉù Ïπ¥Îìú */}
        {step === 'selectMode' && (
          <div className="pre-select-mode-card">
            <img src={logo} alt="ShuttlePlay Î°úÍ≥†" className="pre-card-logo"/>
            <div className="pre-card-content">
              <h2>Îß§Ïπ≠ Î∞©ÏãùÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî!</h2>
              <div className="pre-select-mode-buttons">
                <button
                  className={getBtnClass(step === 'autoInput')}
                  onClick={() => setStep('autoInput')}
                >ÏûêÎèô Îß§Ïπ≠</button>
                <button
                  className={getBtnClass(step === 'filter')}
                  onClick={() => setStep('filter')}
                >ÏàòÎèô Îß§Ïπ≠</button>
              </div>
            </div>
          </div>
        )}

        {/* ÏûêÎèô Îß§Ïπ≠ ÌÅ¥Î¶≠ Ïãú ÎÑòÏñ¥Í∞ÄÎäî Î∂ÄÎ∂Ñ */}
        {step === 'autoInput' && (
          <AutoGameRoomMatching onClose={() => setStep('selectMode')}/>
        )}

        {/* ÏàòÎèô Îß§Ïπ≠ ÌïÑÌÑ∞ */}
        {step === 'filter' && (
          <>
            <div className="pre-filter-bar">
              <div
                className="pre-filter-items"
                style={{display:'flex',gap:20,flexWrap:'wrap',alignItems:'center'}}
              >
                <div className="pre-filter-item">
                  <span>Îß§Ïπ≠Ïú†Ìòï</span>
                  <select value={selectedMode} onChange={e=>{
                    setSelectedMode(e.target.value);
                    setSelectedVenue('');
                  }}>
                    <option value="Íµ¨Ïû•Îß§Ïπ≠">Íµ¨Ïû•Îß§Ïπ≠</option>
                    <option value="ÎèôÎÑ§Îß§Ïπ≠">ÎèôÎÑ§Îß§Ïπ≠</option>
                  </select>
                </div>
                <div className="pre-filter-item">
                  <span>Ïû•ÏÜå</span>
                  <select
                    value={selectedVenue}
                    onChange={e=>setSelectedVenue(e.target.value)}
                  >
                    <option value="">Ïû•ÏÜåÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</option>
                    {selectedMode === 'Íµ¨Ïû•Îß§Ïπ≠'
                      ? gymOptions.map(opt => (
                          <option key={opt.value} value={opt.value}>{opt.label}</option>
                        ))
                      : localOptions.map(opt => (
                          <option key={opt.value} value={opt.value}>{opt.label}</option>
                        ))
                    }
                  </select>
                </div>
                <div className="pre-filter-item">
                  <span>ÎÇ†Ïßú</span>
                  <input
                    type="date"
                    value={selectedDate}
                    onChange={e=>setSelectedDate(e.target.value)}
                  />
                </div>
                <div className="pre-filter-item">
                  <span>ÏãúÍ∞Ñ</span>
                  <select value={selectedTime} onChange={e=>setSelectedTime(e.target.value)}>
                    {timeOptions.map(t => <option key={t}>{t}</option>)}
                  </select>
                </div>
                <div style={{display:'flex',gap:10}}>
                  <button
                    className="pre-filter-apply-btn"
                    onClick={handleFindRooms}
                  >Í≤åÏûÑÎ∞© Î¶¨Ïä§Ìä∏ Ï∞æÍ∏∞</button>
                  <button
                    className="pre-create-btn"
                    onClick={() => setCreateModalOpen(true)}
                  >Î∞© ÏÉùÏÑ±</button>
                </div>
              </div>
            </div>
            {/* Í≤åÏûÑÎ∞© Î¶¨Ïä§Ìä∏ Ïπ¥Îìú Í∑∏Î¶¨Îìú */}
            <div style={{width:'100%'}}>
              {loading && (
                <div className="auto-message">Í≤åÏûÑÎ∞©ÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</div>
              )}
              {error && (
                <div className="auto-message" style={{color:'red'}}>{error}</div>
              )}
              {!loading && !error && roomList.length === 0 && (
                <div className="auto-message">Ï°∞Í±¥Ïóê ÎßûÎäî Í≤åÏûÑÎ∞©Ïù¥ ÏóÜÏäµÎãàÎã§.</div>
              )}
              {!loading && !error && roomList.length > 0 && (
                <div className="pre-card-grid">
                  {roomList.map((room, idx) => (
                    <div className="pre-card" key={room.roomId || idx}>
                      <div className="pre-card-thumbnail">
                        <span className="pre-thumbnail-text">
                          {room.mode === 'Íµ¨Ïû•Îß§Ïπ≠' ? 'üè∏ Íµ¨Ïû• : ' : 'üè† ÎèôÎÑ§ : '} {room.title ? room.title : (room.mode === 'Íµ¨Ïû•Îß§Ïπ≠' ? 'Íµ¨Ïû•' : 'ÎèôÎÑ§')}
                        </span>
                      </div>
                      <div className="pre-card-body">
                        <h4>{room.locationName || 'Ïû•ÏÜå ÎØ∏Ï†ï'}</h4>
                        <div className="pre-alt-info">
                          <div><span className="label">ÎÇ†Ïßú:</span> {selectedDate}</div>
                          <div><span className="label">ÏãúÍ∞Ñ:</span> {selectedTime}</div>
                        </div>
                        <div className="pre-card-bottom">
                          <button
                            className="pre-enter-btn"
                            onClick={() => navigate(`/manual-room/${room.roomId}`)}
                          >ÏûÖÏû•ÌïòÍ∏∞</button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </>
        )}
      </div>

      <div style={{marginTop:'auto'}}><Footer/></div>
    </div>
  );
}

export default PreMatching;
